export default {
  name: "modifyMovie",
  props:{
    mid:{
      type:Number,
      default:0,
    }
  },
  data(){
    return{
      aList:[],
      dList:[],
      movieFrom:{},
      movieName:'',
      movieCover:'',
      movieBio:'',
      movieRegion:[],
      movieTime:'',
      movieLang:[],
      movieRelDate:'',
      actorList:[],
      directorList:[],
      typeList:[],
      fileList:[],
      options:[],
      directOptions:[],
      list:[],
      imageUrl:'',
      loading: false,
      movieFromRules:{
        movieName: [
          {required: true, message: '请输入电影名', trigger: 'blur'},
        ],
      },
    };
  },
  methods:{
    getMovieInfo(){
      this.$axios({
        method:"get",
        url:"/getMovieByMovieId",
        params:{
          mid:this.mid,
        }
      }).then(res=>{
        if (res.data.result){
          this.movieName=res.data.movieInfo.movieName;
          this.movieBio=res.data.movieInfo.movieBio;
          if (res.data.movieInfo.movieLang){
            this.movieLang = res.data.movieInfo.movieLang.split('/');
          }
          if(res.data.movieInfo.movieRegion){
            this.movieRegion = res.data.movieInfo.movieRegion.split('/');
          }
          this.movieTime=res.data.movieInfo.movieTime;
          this.movieRelDate=res.data.movieInfo.movieRelDate;
          this.movieCover = res.data.movieInfo.movieImg;
          this.imageUrl = "http://127.0.0.1:8000/media/" + this.movieCover
          this.actorList = res.data.actorId;
          this.aList=res.data.actor;
          this.dList=res.data.director;
          this.typeList = res.data.typeId;
          this.directorList = res.data.directorId;
          this.imgLoad=true;
        }else{
          this.$message.error('无此电影404');
          this.$router.go(-1);
        }
      })
    },
    checkId() {
      let uid = this.$cookie.get('userId')
      let type = this.$cookie.get('type')
      if (type==1){
        this.$router.go(-1)
      }else{
        this.$axios({
          method:"get",
          url:'/getUserById',
          params:{
            userId:uid,
          },
        }).then(res=>{
          if(res.data.result){
            if(res.data.type){
              this.$cookie.remove('userId');
              this.$cookie.remove('type');
              this.$router.push('/log')
              this.$message.error('非管理员用户，请重新登陆')
            }else{
              this.$message.warning('欢迎登录HERec管理员')
            }
          }else{
            this.$cookie.remove('userId');
            this.$cookie.remove('type');
            this.$router.push('/log')
            this.$message.error('非管理员用户，请重新登陆')
          }
        })
      }
    },
    getPerson(){
      this.$axios({
        method:"get",
        url:'/getPerson',
      }).then(res=>{
        this.list = res.data.personList.map(item=>{
          return{
            value:item[0],
            label:item[1],
          }
        })
      })
    },
    selectActor(query) {
      if (query !== '') {
        this.loading = true;
        setTimeout(() => {
          this.loading = false;
          this.options = this.list.filter(item => {
            return item.label.toLowerCase()
                .indexOf(query.toLowerCase()) > -1;
          });
        }, 200);
      } else {
        this.options = [];
      }
    },
    selectDirector(query) {
      if (query !== '') {
        this.loading = true;
        setTimeout(() => {
          this.loading = false;
          this.directOptions = this.list.filter(item => {
            return item.label.toLowerCase()
                .indexOf(query.toLowerCase()) > -1;
          });
        }, 200);
      } else {
        this.directOptions = [];
      }
    },
    handleAvatarSuccess(res, file) {
      this.imageUrl = URL.createObjectURL(file.raw);
    },
    beforeAvatarUpload(file) {
      let types = ['image/jpeg', 'image/jpg', 'image/png'];
      const isJPG = types.includes(file.type);
      const isLt2M = file.size / 1024 / 1024 < 2;

      if (!isJPG) {
        this.$message.error('上传图片只能是 JPG 格式!');
      }
      if (!isLt2M) {
        this.$message.error('上传头像图片大小不能超过 2MB!');
      }
      return isJPG && isLt2M;
    },
    httpRequest(param){
      let fileObj = param.file
      let fd = new FormData()
      fd.append('file',fileObj)
      fd.append('mid', this.mid)
      this.$axios({
            method: 'post',
            url: '/changeMovieCover',
            data: fd,
            header: {
              'Content-Type': 'multipart/form-data'  //如果写成contentType会报错
            }
          }
      ).then(res => {
        if (res.data.result === false) {
          this.$message.error('修改失败');
          //修改失败，重新注册
        } else {
          this.$message({
            message: '修改封面成功',
            type: 'warning'
          });
        }
      })

    },
    changeMovieTime(){
      console.log(this.movieTime)
      this.$axios({
        method:"get",
        url:'/changeMovieTime',
        params:{
          mid:this.mid,
          mTime:this.movieTime,
        }
      }).then(res=>{
        if(res.data.result){
          this.$message.success('电影时长修改成功')
        }
      })
    },
    changeMovieBio(){
      this.$axios({
        method:"get",
        url:'/changeMovieBio',
        params:{
          mid:this.mid,
          mBio:this.movieBio,
        }
      }).then(res=>{
        if(res.data.result){
          this.$message.success('电影简介修改成功')
        }
      })
    },
    changeMovieRelDate(){
      this.$axios({
        method:"get",
        url:'/changeMovieRelDate',
        params:{
          mid:this.mid,
          mRelDate:this.movieRelDate,
        }
      }).then(res=>{
        if(res.data.result){
          this.$message.success('电影上映时间修改成功')
        }
      })
    },
    changeMovieLang(){
      let mLang=this.movieLang[0]
      for (let i=1;i<this.movieLang.length;i++) {
        mLang = mLang+'/'+ this.movieLang[i]
      }
      console.log(mLang)
      this.$axios({
        method:"get",
        url:'/changeMovieLang',
        params:{
          mid:this.mid,
          mLang:mLang,
        }
      }).then(res=>{
        if(res.data.result){
          this.$message.success('电影语言修改成功')
        }
      })
    },
    changeMovieRegion(){
      let mRegion=this.movieRegion[0]
      for (let i=1;i<this.movieRegion.length;i++) {
        mRegion = mRegion+'/'+ this.movieRegion[i]
      }
      console.log(mRegion)
      this.$axios({
        method:"get",
        url:'/changeMovieRegion',
        params:{
          mid:this.mid,
          mRegion:mRegion,
        }
      }).then(res=>{
        if(res.data.result){
          this.$message.success('电影制片地区修改成功')
        }
      })
    },
    changeMovieType(){
      let tStr=''
      if(this.typeList){
        tStr=this.typeList[0]
        for (let i=1;i<this.typeList.length;i++){
          tStr=tStr+','+this.typeList[i]
        }
      }
      this.$axios({
        method:"get",
        url:'/changeMovieType',
        params:{
          mid:this.mid,
          mType:tStr,
        }
      }).then(res=>{
        if(res.data.result){
          this.$message.success('电影类型修改成功')
        }
      })
    },
    changeMovieDirector(){
      let dStr=''
      if(this.directorList){
        dStr=this.directorList[0]
        for (let i=1;i<this.directorList.length;i++){
          dStr=dStr+','+this.directorList[i]
        }
      }
      this.$axios({
        method:"get",
        url:'/changeMovieDirector',
        params:{
          mid:this.mid,
          mDirector:dStr,
        }
      }).then(res=>{
        if(res.data.result){
          this.$message.success('导演修改成功')
        }
      })
    },
    changeMovieActor(){
      let aStr=''
      if(this.actorList){
        aStr=this.actorList[0]
        for (let i=1;i<this.actorList.length;i++){
          aStr=aStr+','+this.actorList[i]
        }
      }
      this.$axios({
        method:"get",
        url:'/changeMovieActor',
        params:{
          mid:this.mid,
          mActor:aStr,
        }
      }).then(res=>{
        if(res.data.result){
          this.$message.success('演员修改成功')
        }
      })
    },




  },
  created() {
    this.checkId()
    this.getPerson()
    this.getMovieInfo()
  },
  watch:{
    movieRelDate:function (){
      this.changeMovieRelDate()
    },
    movieLang:function (){
      this.changeMovieLang()
    },
    movieRegion:function (){
      this.changeMovieRegion()
    },
    typeList:function (){
      this.changeMovieType()
    },
  },
}
